import { DefaultSession } from "next-auth";

declare module "next-auth" {
    interface Session {
        user: User & DefaultSession["user"]
    }
}

interface User {
    id: string,
    name: string,
    email: string,
    emailVerified?: null| string| boolean,
    image?: string,
    times?: string[],
    stripeCustomerId?: string,
    address?: string,
    phone?: string,
    status: boolean,
    timezone?: string,
    createdAt: string,
    updatedAt: string
}